    background: -webkit-linear-gradient(top,  #f78398 0%,#f67385 100%);
    background: -o-linear-gradient(top,  #f78398 0%,#f67385 100%);
    background: -ms-linear-gradient(top,  #f78398 0%,#f67385 100%);
    background: linear-gradient(top,  #f78398 0%,#f67385 100%);
    filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#f78398', endColorstr='#f67385',GradientType=0 );
}
a.pink:hover {
    background: #f78398;
    background: -moz-linear-gradient(top, #f67385 0%, #f78398 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#f67385), color-stop(100%,#f78398));
    background: -webkit-linear-gradient(top, #f67385 0%, #f78398 100%);
    background: -o-linear-gradient(top, #f67385 0%, #f78398 100%);
    background: -ms-linear-gradient(top, #f67385 0%, #f78398 100%);
    background: linear-gradient(top, #f67385 0%, #f78398 100%);
